// Mock data branch â€” campaign stats for dev/demo
const channels = ['SMS', 'WhatsApp', 'Email', 'Push'];
const projects = ['visionamos', 'demo'];
const records = [];
for (let i = 0; i < 30; i++) {
  const sent = Math.floor(Math.random() * 5000) + 100;
  const delivered = Math.floor(sent * (0.85 + Math.random() * 0.14));
  records.push({
    date: new Date(Date.now() - i * 86400000).toISOString().slice(0, 10),
    canal: channels[Math.floor(Math.random() * channels.length)],
    proyecto_cuenta: projects[Math.floor(Math.random() * projects.length)],
    enviados: sent,
    entregados: delivered,
    clicks: Math.floor(delivered * Math.random() * 0.2),
    chunks: Math.floor(Math.random() * 3) + 1,
    tenant_id: 'demo'
  });
}
return records.map(r => ({json: r}));
