// Mock data branch â€” used when API key is not configured
// Generates sample records for development/demo
const mockMessages = [];
for (let i = 0; i < 50; i++) {
  mockMessages.push({
    message_id: `mock_msg_${i}`,
    timestamp: new Date(Date.now() - Math.random() * 7776000000).toISOString(),
    direction: ['Inbound', 'Bot', 'Agent', 'Outbound'][Math.floor(Math.random() * 4)],
    contact_id: `contact_${Math.floor(Math.random() * 50)}`,
    contact_name: `Usuario ${Math.floor(Math.random() * 50)}`,
    is_fallback: Math.random() < 0.12,
    tenant_id: 'demo'
  });
}
return mockMessages.map(m => ({json: m}));
